rule.example:
public class Foo {
	public void bar(Integer x, Integer y, Integer z) {
		if (x>y) {
			if (y>z) {
				if (z==x) {
					// !! too deep
				}
			}
		}
	}
}

// too many arguments liable to be mixed up
public void addPerson(int birthYear, int birthMonth, int birthDate, int height, int weight, int ssn) {
	...
}
// preferred approach 
public void addPerson(Date birthdate, BodyMeasurements measurements, int ssn) {
	...
}

public class Foo {
	public void bar1() {
		// 1000 lines of code
	}
	public void bar2() {
		// 1000 lines of code
	}
	public void bar3() {
		// 1000 lines of code
	}
	public void barN() {
		// 1000 lines of code
	}
}

public class Foo extends Bar {
	//this method only has 1 NCSS lines
	public Integer methd() {
		super.methd();



		return 1;
	}
}

//this class only has 6 NCSS lines
public class Foo extends Bar {
	public Foo() {
		super();





		super.foo();
	}
}

public class Foo extends Bar {
	//this constructor only has 1 NCSS lines
	public Foo() {
		super();




		super.foo();
	}
}

// This has a Cyclomatic Complexity = 12
	public class Foo {
1		public void example() {
2			if (a == b || (c == d && e == f)) {
3				if (a1 == b1) {
					fiddle();
4				} else if a2 == b2) {
					fiddle();
				}  else {
					fiddle();
				}
5			} else if (c == d) {
6				while (c == d) {
					fiddle();
				}
7			} else if (e == f) {
8				for (int n = 0; n < h; n++) {
					fiddle();
				}
			} else {
				switch (z) {
9					case 1:
						fiddle();
						break;
10					case 2:
						fiddle();
						break;
11					case 3:
						fiddle();
						break;
12					default:
						fiddle();
						break;
				}
			}
		}
	}

public class Person {
	// too many separate fields
	int birthYear;
	int birthMonth;
	int birthDate;
	float height;
	float weight;
}

public class Person {
	// this is more manageable
	Date birthDate;
	BodyMeasurements measurements;
}

