class Foo {
  Boolean a(Integer n) {
    return n > 0 && n > 1;          // +1
  }

  Boolean b(Integer n) {
    return n > 0 && n > 1 && n > 2; // +1
  }

  Boolean c(Integer n) {
    return n > 0 || n < 0;          // +1
  }

  Boolean d(Boolean a, Boolean b, Boolean c, Boolean d, Boolean e, Boolean f) {
    return (a
        && b && c)                 // +1
        || (d || e)                // +1
        && f;                      // +1
  }

  Boolean e(Boolean a, Boolean b, Boolean c) {
    return a
        &&                        // +1
        !(b && c);                // +1
  }

  Boolean f() {
    Boolean a = true && false;    // +1
    return a && true;             // +1
  }
}


class Foo {
  Integer foo(Integer n) {
    if (n == 0 || n == 1) { // +2
      return 1;
    }

    return n * foo(n - 1); // +1
  }
}


