test-code.code:
public class Foo {
    public void bar() {
        try {
            system.debug(1);
        } catch(Exception e) {
        }
    }
}

public class Foo {
    public void foo() {
        try {
            system.debug(1);
        } catch(Exception e) {
            system.debug(e);
        }
    }
}

@IsTest
private class FunctionalityTest {
    static testMethod void testWithModifier() {
        try {
            doSomethingThatShouldThrowAnException();
            System.assert(false, 'Expected an exception to be thrown.');
        } catch (Exception e) {
        }
    }

    @IsTest
    static void testWithAnnotation() {
        try {
            doSomethingThatShouldThrowAnException();
            System.assert(false, 'Expected an exception to be thrown.');
        } catch (Exception e) {
        }
    }
}

