rule.example:
public void doSomething() {
    try {
      FileInputStream fis = new FileInputStream("/tmp/bugger");
    } catch (IOException ioe) {
        // not good
    }
  }

if (foo == 0) {
   // why bother checking up on foo?
}

while (a == b) {
  // not good
}

// this is bad
public void bar() {
    try {
    } catch (Exception e) {
        e.printStackTrace();
    }
}

// this is bad
public void bar() {
    try {
        int x=2;
    } finally {
    }
}

public class Foo {
 public void bar() {
  int x = 2;
  switch (x) {
   // once there was code here
   // but it's been commented out or something
  }
 }
}

public class JumbledIncrementerRule1 {
 public void foo() {
  for (int i = 0; i < 10; i++) {
   for (int k = 0; k < 20; i++) {
    System.out.println("Hello");
   }
  }
 }
}

public class Foo {
    void bar() {
        for (;true;) true; // No Init or Update part, may as well be: while (true)
    }
}

public String convert(int x) {
    // this wastes an object
    String foo = new Integer(x).toString();
    // this is better
    return Integer.toString(x);
  }

// this is bad
public class Bar {
    public boolean equals(Object o) {
        // do some comparison
    }
}

// and so is this
public class Baz {
    public int hashCode() {
        // return some hash value
    }
}

// this is OK
public class Foo {
    public boolean equals(Object other) {
        // do some comparison
    }
    public int hashCode() {
        // return some hash value
    }
}

public class Foo {
    Object baz;
    Object bar() {
      if(baz == null) { //baz may be non-null yet not fully created
        synchronized(this){
          if(baz == null){
            baz = new Object();
          }
        }
      }
      return baz;
    }
}

  public class Bar {
 public String bugga() {
  try {
   throw new Exception( "My Exception" );
  } catch (Exception e) {
   throw e;
  } finally {
   return "A. O. K."; // Very bad.
  }
 }
}

// this is bad
public void bar() {
    synchronized (this) {}
}

// this is bad
public void bar() {
    int x = 42;
    return;
}

  public class Foo {
 // why are there no statements in this static block?
 static {}
}

  public class Foo {
 public void close() {
  if (true) {
       // ...
   }
 }
}

public class MyClass {
   public void doit()
   {
      // this is probably not what you meant to do
      ;
      // the extra semicolon here this is not necessary
      System.out.println("look at the extra semicolon");;
   }
}

property.value:
//PrimaryExpression
[
PrimaryPrefix/AllocationExpression[not (ArrayDimsAndInits) and (ClassOrInterfaceType/@Image='Boolean' or ClassOrInterfaceType/@Image='java.lang.Boolean')]
or
(
PrimaryPrefix/Name[@Image='Boolean.valueOf']
and
PrimarySuffix/Arguments//BooleanLiteral
)
]

rule.example:

public class Foo {
 private Boolean bar = new Boolean("true"); // just do a Boolean bar = Boolean.TRUE;
}

public final class Foo {

    // This final modifier is not necessary, since the class is final
    // and thus, all methods are final
	private final void foo() {
	}

}

public class Foo {
    void bar() {
        if (x) {
         if (y) {
           // do stuff
         }
        }
    }
}
