property.value:
//PrimaryExpression [ PrimaryPrefix [Name[ends-with(@Image, 'toLowerCase') or ends-with(@Image, 'toUpperCase')]] [following-sibling::PrimarySuffix[position() = 1]/Arguments[@ArgumentCount=0]]
or
PrimarySuffix [ends-with(@Image, 'toLowerCase') or ends-with(@Image, 'toUpperCase')] [following-sibling::PrimarySuffix[position() = 1]/Arguments[@ArgumentCount=0]] ] [not(PrimaryPrefix/Name[ends-with(@Image, 'toHexString')])]

rule.example:

class Foo {
    // BAD
    if (x.toLowerCase().equals("list")) { }

    /*
     * This will not match "LIST" when in Turkish locale
     * The above could be
     * if (x.toLowerCase(Locale.US).equals("list")) { }
     * or simply
     * if (x.equalsIgnoreCase("list")) { }
     */
    // GOOD
    String z = a.toLowerCase(Locale.EN);
}

