rule.example:

if (foo)    // not recommended
    x++;

if (foo) {  // preferred approach
    x++;
}

while (true)    // not recommended
      x++;

while (true) {  // preferred approach
      x++;
}

   // this is OK
if (foo) x++;

   // but this is not
if (foo)
       x = x+1;
   else
       x = x-1;
