public class Foo {
  public String foo(int n) {
    if (n == 1) {
      return "one";
    }

    return "not one";
  }
}


public class Foo {
  public String foo(int n) {
    if (n > 0) {           // +1
      if (n == 1) {        // +2
        return "one";
      }

      return "positive";
    }

    return "negative or 0";
  }
}


public class Foo {
  public String foo(int n) {
    if (n > 0) {           // +1
      if (n == 1) {        // +2
        return "one";
      } else {             // +1
        return "positive";
      }
    }

    return "negative or 0";
  }
}


public class Foo {
  public String foo(int n) {
    if (n > 0) {           // +1
      if (n == 1) {        // +2
        return "one";
      } else if (n == 0) {  // +1
        return "zero";
      }
    }

    return "negative or 0";
  }
}


public class Foo {
  public String foo(int n) {
    if (n > 0) {           // +1
       return "positive";
    } else if (n < 0) {   // +1
        if (n < -10) {        // +2
            return "small";
        }
    }

    return "negative or 0";
  }
}


