public class Foo {
  public String foo(int n) {
    if (n > 0) {           // +1
      return "positive";
    }

    if (n == 0) {          // +1
      return "zero";
    }

    if (n < 0) {           // +1
      return "negative";
    }

    return "";
  }
}


