    } else if (n < 0) {    // +1
      return "negative";
    } else {               // +1
      return "zero";
    }
  }
}


public class Foo {
  public String foo() {
    for (int i = 0; i < 10; i++) { // +1
      if (i == 3) {                // +2
        return "three";
      }
    }

    return "done";
  }
}


public class Foo {
  public String foo() {
    int[] myInts = new int[] {1, 2, 3};
    for (Integer i : myInts) {         // +1
      if (i == 3) {                    // +2
        return "three";
      }
    }

    return "done";
  }
}


public class Foo {
  public String foo() {
    int[] myInts = new int[] {1, 2, 3};
    for (int i : myInts) {         // +1
      if (i == 3) {                // +2
        continue;                  // +1
      }
    }

    return "done";
  }
}


public class Foo {
  public String foo() {
    int[] myInts = new int[] {1, 2, 3};
    for (Integer i : myInts) {      // +1
      if (i == 3) {                 // +2
        break;                      // +1
      }
    }

    return "done";
  }
}


public class Foo {
  public String foo() {
    int i = 1;
    int n = 0;
    while (n < 100) {  // +1
      i = i * n;
      if (i > 1000) {  // +2
        return "big";
      }
    }

    return "small";
  }
}


public class Foo {
  public void foo() {
    List<Integer> ints = new ArrayList<Integer>();
    try {
      Integer x = ints.get(10);
      if (x > 0) {                           // +1
        return;
      }
    } catch (IndexOutOfBoundsException e) {  // +1
      if (ints.size() > 0) {                 // +2
        return;
      }
    }
  }
}


public class Foo {
  public void foo() {
    int n = 0;
    do {                                 // +1
      if (n == 3) {                      // +2
        System.out.println("n is 3");
      } else {                           // +1
        System.out.println("n is not 3");
      }
      n++;
    } while (n < 100);
  }
}


public class Foo {
  public int foo(int n) {
    return n < 0 ?          // +1
        -1 : n > 0 ?        // +2
        1 : 0;
  }
}


public class Foo {
  public boolean a(Integer n) {
    return n > 0 && n > 1;          // +1
  }

  public boolean b(Integer n) {
    return n > 0 && n > 1 && n > 2; // +1
  }

  public boolean c(Integer n) {
    return n > 0 || n < 0;          // +1
  }

  public boolean d(Boolean a, Boolean b, Boolean c, Boolean d, Boolean e, Boolean f) {
    return (a
        && b && c)                 // +1
        || (d || e)                // +1
        && f;                      // +1
  }

  public boolean e(Boolean a, Boolean b, Boolean c) {
    return a
        &&                        // +1
        !(b && c);                // +1
  }

  public boolean f() {
    Boolean a = true && false;    // +1
    return a && true;             // +1
  }
}


public class Foo {
  public int foo(Integer n) {
    if (n == 0 || n == 1) { // +2
      return 1;
    }

    return n * foo(n - 1); // +1
  }
}


public class Foo {
  public int foo(Integer n) {
    if (                    // +1
        n == 0 || n == 1    // +1
    ) {
      return 1;
    }
    return 0;
  }
}


public class Foo {
  public void foo(int n) {
    switch(n) {                       // +1
      case 1:
        System.out.println("when block 1");
        break;
      case 2:
      case 3:
      case 4:
      case 5:
        if (n <= 3) {                   // +2
          System.out.println("n <= 3");
        }

        System.out.println("when block 2");
        break;
      default:
        System.out.println("default");
        break;
    }
  }
}


public class Foo {
  public void foo(int n) {
    Runnable r = new Runnable() {  // +0 (but nesting level is now 1)
      @Override
      public void run() {
        if ( n > 1) {              // +2 (nesting=1)

        }
      }
    };
  }
}


public class Foo {
  public void foo(int n) {
    Runnable r = () -> { // +0 (but nesting level is now 1)
      if ( n > 1) {      // +2 (nesting=1)

      }
    };
  }
}
