public class Complicated {

  int x = 0, y = 1, z = 2, t = 2;
  boolean a = false, b = true, c = false, d = true;

  public void exception() {
    try {
      int k = 0;
      k++;
    } catch (IOException ioe) {
      ioe.printStackTrace();
      throw new Exception("surprise", ioe);
    } catch (Exception e) {
      // do nothing
    }
  }

  public void aSwitch(int x) {
    switch (x) {
    case 1:
      x = 2;
      break;

    case 2:
    case 3:
      x = 2;
      break;

    default:
      x = 2;
      break;
    }
  }

  public void aDo(int x, int y) {
    do {
      x++;
      y--;
    } while (x != y);
  }

  public void anAssert() {
    assert a : "fail";
    assert b || a : "fail";
  }

  public void aFor() {
    for (int n = 0; n < t; n++) {
      x = 2;
    }
  }

  public void aWhile() {
    while (z < y) {
      x = 2;
    }
  }

  public void ternaries() {
    x = a ? z : y == z && true ? y : x;
  }


  public void booleans() {

    if (a && b || b && d) {
      ternaries(x, y, z, a);
    } else if (c && d) {
      aFor(t, x);
    } else if (a && !b) {
      aWhile(x, y, z);
    } else if (b) {
      aDo(x, y);
    } else {
      aSwitch(x);
    }
  }
}


