test-data.code-fragment:

public class Complicated {
  int x = 0, y = 1, z = 2, t = 2;
  boolean a = false, b = true, c = false, d = true;

  public void example1() {
    if (a && b || b && d) {
      if (y == z) {
        x = 2;
      } else if (y == t && !d) {
        x = 2;
      } else {
        x = 2;
      }
    }
  }

  public void example2() {
    if (c && d) {
      while (z < y) {
        x = 2;
      }
    }
  }

  public void example3() {
    if (a && !b) {
      for (int n = 0; n < t; n++) {
        x = 2;
      }
    }
  }

  public void example4() {
    switch (x) {
    case 1:
      x = 2;
      break;

    case 2:
    case 3:
      x = 2;
      break;

    default:
      x = 2;
      break;
    }
  }
}

