abstract class Foo {
  void foo() {
    if (true) {
      foo();
    }
  }

  enum Bar {
    BAR, NA, BEE;

    void bar() {
      switch (this) {
        case BAR:
        case NA:
        case BEE: break;
      }
    }
  }
}

