public class Foo {
volatile Object baz;

Object bar() {
    if (baz == null) { // baz may be non-null yet not fully created
        synchronized (this) {
            if (baz == null) {
                baz = new Object();
            }
        }
    }
    return baz;
}

}

public class Foo {
    private static volatile Foo instance;

    public static Foo getInstance() {
        Foo result = instance;
        if (result == null) {
            synchronized (Foo.class) {
                result = instance;
                if (result == null) {
                    result = instance = new Foo();
                }
            }
        }
        return result;
    }
}
