public class Foo1 {
 public class InnerClass {
   private InnerClass(){
   }
 }
 void method(){
   new InnerClass();//Causes generation of accessor
 }
}

public class Foo2 {
 public class InnerClass {
   public InnerClass(){
   }
 }
 void method(){
   new InnerClass(); //OK, due to public constructor
 }
}

public class Foo3 {
    public class InnerClass {
      void method(){
        new Foo3();//Causes generation of accessor
      }
    }
    private Foo3() {
    }
}

