import java.sql.*;

public class Foo {
    public void bar(Statement stmt) {
        ResultSet results = stmt.executeQuery();
        boolean hasNext = results.next();
        while (hasNext) {
            System.out.println("next!");
            hasNext = results.next();
        }
    }
}

import java.sql.*;

public class Foo {
    public void bar(Statement stmt) {
        ResultSet results = stmt.executeQuery();
        boolean hasNext = results.next();
        hasNext = false;
        if (hasNext) {
            System.out.println("next!");
        }
    }
}

import java.sql.Connection;
import java.sql.ResultSet;

class Scratch {
    public void bar(Connection c, String tableName) throws Exception {
          ResultSet created = c.getMetaData().getTables(null, null, tableName, null);
          boolean missing = !created.next();
          reportTableName(tableName, !missing);
    }

    private void reportTableName(String tableName, boolean alreadyExisting) {
        if (!alreadyExisting) {
            System.out.println("Table " + tableName + " is available");
        }
    }
}

import java.sql.*;
import java.util.function.Consumer;

class Foo {
    public void bar(ResultSet resultSet) {
        int x = consumeResultSet(resultSet, rs -> {
            rs.next();
        });
        System.out.println(x);

        int y = consumeResultSet(resultSet, new Consumer<ResultSet>() {
            @Override
            public void accept(ResultSet rs) {
                rs.next();
            }
        });
        System.out.println(y);
    }

    private static int consumeResultSet(ResultSet resultSet, Consumer<ResultSet> consumer) throws Exception {
        consumer.accept(resultSet);
        return rs.getInt(0);
    }
}
