enum Color { RED, GREEN, BLUE }
class ColorTester {
  boolean isRed(Color color) {
    return color.equals(Color.RED); // violation
  }

  boolean isGreen(Color color) {
    return color == Color.GREEN; // preferred
  }
}

class Foo {
  void method(Thread.State state1, Thread.State state2) {
    if (state1.equals(state2)) { }  // violation

    if (state1 == state2) {}  // correct
  }
}

import java.lang.annotation.RetentionPolicy;

class Foo {
    void method() {
        Object x = new Object();
        if (RetentionPolicy.CLASS.equals(x)) {} // violation, line 6
        if (x.equals(RetentionPolicy.CLASS)) {} // violation, line 7

        RetentionPolicy y = RetentionPolicy.CLASS;
        if (x.equals(y)) {} // violation, line 10
        if (y.equals(x)) {} // violation, line 11

        String s = "a";
        if (x.equals(s)) {} // no violation, no enums involved
        if (s.equals(x)) {} // no violation, no enums involved
    }
}

import java.lang.annotation.ElementType;
import java.lang.annotation.RetentionPolicy;

class Foo {
    void method() {
        ElementType x = ElementType.TYPE_USE;
        RetentionPolicy y = RetentionPolicy.CLASS;

        if (x.equals(y)) {} // violation, line 9
        if (y.equals(x)) {} // violation, ine 10

        // note: this is compilation error (incompatible types)
        if (x == y) {}
    }
}
