import java.util.List;
import java.util.stream.Collectors;

class SomeTestClass {
    void example(List<String> strings) {
        var list = strings.stream().map(t -> t + ".java").collect(Collectors.toList());
        //  can be replaced with .toList() since Java 16 ------------------------------
    }
}

import java.util.List;
import java.util.stream.Collectors;

class SomeTestClass {
    void example(List<String> strings) {
        var list = strings.stream().map(t -> t + ".java").collect(Collectors.toUnmodifiableList());
    }
}

import java.util.Arrays;
import java.util.List;
import java.util.stream.Collectors;

class SomeTestClass {
    void example(List<String> strings) {
        var list = Arrays.asList("a", "b").stream();
    }
}
