public class Foo {
    void bar() {
        int x = 2;
        switch (x) {
            case 2: int y=8;
        }
    }
}

public class Foo {
    void bar() {
        int x = 2;
        switch (x) {
            case 2: int y=8;
            default: int j=8;
        }
    }
}

import net.sourceforge.pmd.lang.java.rule.bestpractices.switchstmtsshouldhavedefault.SimpleEnum;

public class Foo {
    void bar() {
        SimpleEnum a;

        // This switch is NOT exhaustive
        switch (a) {
        case BZAZ:
            int y = 8;
            break;
        }
    }
}

import net.sourceforge.pmd.lang.java.rule.bestpractices.switchstmtsshouldhavedefault.SimpleEnum;

public class Foo {
    void bar() {
        SimpleEnum x;

        // This switch is exhaustive
        switch (x) {
        case BZAZ:
            int y = 8;
            break;
        case FOO:
            break;
        case BAR:
            int w = 8;
            break;
        }
    }
}

public class Foo {
    enum MyEnum { A, B, C }
    void bar(MyEnum x) {
        // This switch is not exhaustive
        switch (x) {
        case A:
            int y = 8;
            break;
        case B:
            break;
        }
    }
}

public class Foo {
    enum MyEnum { A, B, C }
    void bar(MyEnum x) {
        // This switch is not exhaustive
        switch (x) {
        case A -> {
            int y = 8;
        }
        case B -> throw new IllegalStateException();
        }
    }
}

import net.sourceforge.pmd.lang.java.rule.bestpractices.switchstmtsshouldhavedefault.SimpleEnum;

public class Foo {
    public String foo(String foo) {
        switch (foo) {
        case "=" -> foo = "eq";
        case "!=" -> foo = "ne";
        default -> throw new IllegalArgumentException();
        }
        return foo + 'a';
    }
}

public enum GradeSystem {
    SysA, SysB;

    public boolean isValid() {
        switch (this) { // false-positive
        case SysA:
        case SysB:
            return true;
        }

        return false;
    }
}
