import java.util.Arrays;

public class Foo {
    static {
        Arrays.asList("a" , "b");
        Arrays.asList(new String[] { "a", "b" });
        Arrays.asList(new Object[] { "a", "b" });
    }
}

import java.util.Arrays;

public class Foo {
    static {
        Arrays.asList(new String[]{""}, new String[] { "a", "b" });
    }
}

import java.util.Arrays;

public class Foo {
    static {
        foo(new Object[]{ "a" }); // regular unnecessary
        foo(new String[]{ "a" }); // confusing bc String[] <: Object (ConfusingArgumentToVarargsArray)
    }
    static void foo(Object... args) {}
}

import java.util.Arrays;

public class Foo {
    static {
        foo(new Object[4]);
    }
    static void foo(Object... args) {}
}
