public class Sample {

                public ZDT sample(final String date, final ZDT paymentTimeStamp) {
                    final ZDT startOfDayOnInceptionDate = zonedDateTimeAtOnePastMidnight(date, paymentTimeStamp.getZone());
                    final ZDT startOfDayOnPaymentDate = zonedDateTimeAtOnePastMidnight(paymentTimeStamp.toLocalDate(), paymentTimeStamp.getZone());
                    if (startOfDayOnInceptionDate.isAfter(paymentTimeStamp)) {
                        return startOfDayOnInceptionDate;
                    } else if (paymentTimeStamp.isAfter(startOfDayOnPaymentDate)) {
                        return paymentTimeStamp;
                    } else {
                        return startOfDayOnPaymentDate;
                    }
                }

                public ZDT zonedDateTimeAtOnePastMidnight(final String date, final ZoneId zoneId) {
                    return zonedDateTimeAtOnePastMidnight(parseStringWithIsoDateFormat(date), zoneId);
                }

                private ZDT zonedDateTimeAtOnePastMidnight(final LocalDate localDate, final ZoneId zoneId) {
                    return ZDT.of(localDate, zoneId);
                }

                LocalDate parseStringWithIsoDateFormat(String s) {return null;}
                static abstract class ZDT {
                    abstract boolean isAfter(ZDT zdt);
                    abstract boolean isBefore(ZDT zdt);
                    abstract ZoneId getZone();
                    abstract LocalDate toLocalDate();
                    static ZDT of(LocalDate date, ZoneId zone) {return null;}
                }
                static class LocalDate { }
                static enum ZoneId { }
            }

