public final class Fib {
    /** utility class */
    private Fib() {}

    public static int fib(final int n) {
        checkArgument(n >= 0);

        if (n < 2) {
            return n;
        } else {
            int a = 0;
            int b = 1;
            final int m = n - 1;

            for (int i = 0; i < m; i++) {
                final int c = a;
                a = b;
                b = c + b;
            }

            return b;
        }
    }
}

import java.lang.reflect.InvocationTargetException;

public class Formatter {
                public Formatter test(String formatterClassName) {
                    Formatter formatter = null;
                    if (formatterClassName != null) {
                        try {
                            Class<? extends Formatter> formatterClass = (Class) Class.forName(formatterClassName);
                            formatter = formatterClass.getDeclaredConstructor().newInstance();
                        } catch (ClassNotFoundException | NoSuchMethodException | InvocationTargetException | IllegalAccessException | InstantiationException ignored) {
                        }
                    }
                    return ((formatter == null) ? new Formatter() : formatter);
                }
            }

import java.util.Map;
import java.util.List;

public class Test {

    Map<String, List<String>> bufferedMessages;

    static class LogEvent { int timeMillis; String message; }
    static class DiscordManager { }
    void sendMessage(DiscordManager discordManager, String message) {}
    void bufferMessage(String message, int time) {}

    public void append(LogEvent event, DiscordManager discordManager) {
        String logMessage = event.message;
        if (discordManager == null) {
            bufferMessage(logMessage, event.timeMillis);
        } else {
            bufferedMessages.computeIfPresent("getName()", (name, bufferedMessages) -> {
                bufferedMessages.forEach(bufferedMessage -> sendMessage(discordManager, bufferedMessage));
                return null;
            });
            sendMessage(discordManager, logMessage);
        }
    }
}

