public class Switcher {
    public record Wrap(int k) {
    }
    public static void foo(Object r) {
        switch (r) {
            case Wrap(int n) when n < 0 -> System.out.println("a wrapped negative int");
            default -> System.out.println("not a wrapped int");
        }
    }
}

public class Reproducer {
    private String barString;
    public static boolean customEquals(Object foo, Object bar) {
        return switch (foo) {
            case String fooString when bar instanceof String barString -> fooString.equals(barString);
            default -> false;
        };
    }
}

public class Reproducer {
    private String barString;
    public static boolean customEquals(Object foo, Object bar) {
        return switch (foo) {
            case String fooString when !(bar instanceof String barString) -> fooString.equals(barString);
            default -> false;
        };
    }
}

