class Foo {
    {
        do {
        } while (true);
        do {
        } while ((true));
    }
}

class Foo {
    {
        do { } while (true | true);
        do { } while (true || true);
        do { } while ((true | true));
        do { } while ((true || true));

        boolean otherBool = false;
        do { } while (true | otherBool);
        do { } while (true || otherBool);
        do { } while ((true | otherBool));
        do { } while ((true || otherBool));
    }
}

