    {
        do {
        } while (false & false);
        do {
        } while ((false & false));
        do {
        } while (false && false);
        do {
        } while ((false && false));

        boolean otherBool = true;
        do {
        } while (false & otherBool);
        do {
        } while ((false & otherBool));
        do {
        } while (false && otherBool);
        do {
        } while ((false && otherBool));
    }
