class Foo {
    {
        do {
        } while (true & true);
        do {
        } while ((true & true));
        do {
        } while (true && true);
        do {
        } while ((true && true));

        do {
        } while (true & true & true);
        do {
        } while ((true & true & true));
        do {
        } while (true && true && true);
        do {
        } while ((true && true && true));

        // the following loops are not reported, because they depend on otherBool
        boolean otherBool = true;
        do {
        } while (true & otherBool);
        do {
        } while ((true & otherBool));
        do {
        } while (true && otherBool);
        do {
        } while ((true && otherBool));
    }
}

class Foo {
    {
        while (false | false) { }
        while ((false | false)) { }
        while (false || false) { }
        while ((false || false)) { }

        while (false | false | false) { }
        while ((false | false | false)) { }
        while (false || false || false) { }
        while ((false || false || false)) { }
    }
}

class Foo {
    void bar(boolean arg) {
        while (false | arg) { }
        while (false || arg) { }
        do { } while (false || arg);
    }
}

class Foo {
    void bar(boolean arg) {
        while (false & arg) { }
        while (false && arg) { }
        do { } while (false && arg);
    }
}

class Foo {
    {
        // these evaluate to true and are allowed
        while (true & true) { }
        while ((true & true)) { }
        while (true && true) { }
        while ((true && true)) { }

        // these evaluate to false and should be flagged
        while (false & false) { } // line 10
        while ((false & false)) { }
        while (false && false) { }
        while ((false && false)) { }
        while (false & true) { }
        while ((false & true)) { }
        while (false && true) { }
        while ((false && true)) { }
        while (true & false) { }
        while ((true & false)) { }
        while (true && false) { }
        while ((true && false)) { }

        // do-while loops should always be flagged
        do {} while (false & false);
        do {} while ((false & false));
        do {} while (false && false);
        do {} while ((false && false));
        do {} while (true & false);
        do {} while ((true & false));
        do {} while (true && false);
        do {} while ((true && false));
        do {} while (false & true);
        do {} while ((false & true));
        do {} while (false && true);
        do {} while ((false && true));
        do {} while (true & true);
        do {} while ((true & true));
        do {} while (true && true);
        do {} while ((true && true));
    }
}

public class Foo {
    public void func() {
        do {
            // Loop Body
        } while (false || false || false || false); // This is a false negative

        do {
            // Loop Body
        } while (false | false | false | false | false); // This is a similar false negative.

        do {            //reported: WhileLoopWithLiteralBoolean:    The loop can be simplified.
            // Loop Body
        } while (false && false && false && false);

        do {            //reported: WhileLoopWithLiteralBoolean:    The loop can be simplified.
            // Loop Body
        } while (false & false & false & false & false);
    }
}

public class Foo {
    public void func(int x, int y) {
        do {
            System.out.println("Testing");
        } while (x==0 || y==2);
    }
}
