import com.google.common.annotations.VisibleForTesting;

public class CommentDefaultAccessModifier {
    @VisibleForTesting void method() {}
}

public enum Bar {
    ONE, TWO;

    Bar() {}
}

public class Foo {
    enum Bar {
        ONE, TWO;

        Bar() {}
    }
}

public class Foo {
    @interface Bar {
        String baz();
    }
}

public interface A {
    interface B {
        default void b() {}
    }
}

