public interface InterfaceWithDefaultMethod {
    default void bar() {
        String a = "a";
        System.out.println(a);
    }
}

public interface InterfaceWithClass {
    class Inner {
        void bar() {
            String a = "a";
            System.out.println(a);
        }
    }
}

public class ClassWithAnon {
    void method() {
        final ClassWithAnon anon = new ClassWithAnon() {
            void method() {
                String a = "a";
                System.out.println(a);
            }
        };
        final Runnable runnable = new Runnable() {
            public void run() {
                int a = 0; //unused so no warning
            }
        };
    }
}

public class ClassWithLambda {
    void method() {
        final Runnable runnable = () -> {
            int a = 0;
            System.out.println(a);
        };
    }
}

public class ClassWithLambda {
    void method() {
        for (int i = 0, size = loaders.size(); i < size; ++i);
    }
}

public class ClassWithLambda {
    void method() {
        int a = 2; // nowarn
        a = 4;
        System.out.println(a);

        int b; // nowarn
        b = 1 + 2;
        b = 4;
        System.out.println(b);

        int c; // warn
        c = 1 + 2;
        System.out.println(c);

        int x;
        if (getClass().isAnonymousClass()) {
            x = 2;
        } else {
            x = 4;
        }
        // x is unused

        int y; // warn
        if (getClass().isAnonymousClass()) {
            y = 2;
        } else {
            y = 4;
        }
        System.out.println(y);
    }
}

public class ClassWithLambda {

    void expandKey(byte key[]) {
        int octet;
        byte ek[] = new byte[128];

        octet = key[0];
        if ((octet & 0x80) != 0) {
            ek[104] |=  4; ek[122] |= 32;
        }

        octet = key[1];
        if ((octet & 0x80) != 0) {
            ek[  1] |=  8; ek[  8] |= 32; ek[ 17] |=  1;
            ek[ 24] |= 16; ek[ 35] |=  4; ek[ 50] |=  1;
            ek[ 57] |= 16; ek[ 67] |=  8; ek[ 83] |=  1;
            ek[ 88] |=  1; ek[ 98] |=  4; ek[105] |= 32;
            ek[114] |= 32; ek[123] |=  2;
        }
    }
}
