public class MethodNamingConventions implements SomeUnknownInterface {
    @Override
    public void _foo() {
    }
}

class Foo {
    static void foo() {
    }

    static void st_whatever() {
    }

    // not reported
    void bar() {
    }
}

class Foo {
    native void foo() {
    }

    native void nt_whatever() {
    }

    // not reported
    void bar() {
    }
}

