public class ShortVariable {
    public void bar() {
        String thisIsOk = "";
        String foobar = ""; // too short
    }
}

public class ShortVariable {
    public void bar() {
        String thisIsOk = "";
        String foo = ""; // that's ok, too, now
    }
}

public class ShortVariable {
    public void bar() {
        String thisIsOk = a -> foo();
        String foo = (a, b) -> foo();
        String bar = (String a, Boolean b) -> foo();
    }
}
