public class Foo {
    void bar() {
        if (false) return;
    }
}

public class Foo {
    abstract void bar();
}

public class Foo {
    void bar() {
        try {
        } catch(Exception e){
            return;
        }
    }
}

public class Foo {
    void bar() {
        try {
        } catch(Exception e){
            return;
        }
        toString();
    }
}

public class Foo {
    static {
        try {
        } catch(Exception e){
            return;
        }
        toString();
    }
}

public class Foo {
    static {
        try {
        } catch(Exception e){
            return;
        }
    }
}

public class Foo {
    static {
        try {
        } catch(Exception e) {
            return;
        } finally {
            printSomething();
        }
    }
}

public class Foo {
    static {
        int i;
        if (foo()) {
            i = 0;
            return;
        } else if (bar()) {
            i = 1;
            return;
        }
    }
}

public class Foo {
    static {
        int i;
        if (foo()) {
            i = 0;
            return;
        } else if (bar()) {
            i = 1;
            return;
        }
        System.out.println(i);
    }
}

public class Foo {
    {
        int i, j;
        switch (i) {
        case 1:
            return;
        case 2:
            j = 2;
        case 4:
            j = 5;
            return; // this one is unnecessary
        }
    }
}

public class Foo {
    // Note that those blocks are useless, they just limit the scope of locals,
    // and don't act on control flow. So this is identical as the above.
    {
        int i, j;
        switch (i) {
        case 1: {
            return;
        }
        case 2: {
            j = 2;
        }
        case 4: {
            j = 5;
            return; // this one is unnecessary
        }
        }
    }
}

public class Foo {

    static {
        int i, j;
        switch (i) {
        case 1 -> {
            j = 1;
            return; // unnecessary
        }
        case 2 -> {
            j = 2;
        }
        case 4 -> {
            j = 5;
            return; // unnecessary
        }
        }
    }
}

public class Foo {

    static {
        int i, j;
        switch (i) {
        case 1 -> {
            j = 1;
            return; // necessary
        }
        case 2 -> {
            j = 2;
        }
        case 4 -> {
            j = 5;
            return; // necessary
        }
        }
        System.out.println(j);
    }
}

public class Foo {

    static {
        int i, j;
        int k = switch (i) {
            case 1 -> {
                return; // necessary
            }
            case 2 -> {
                j = 2;
            }
            case 4 -> {
                j = 5;
                return; // necessary
            }
        };
    }
}
