public class Foo {
    synchronized void foo () {}
}

public class Foo {
    void foo () {
        synchronized(mutex) {}
    }
}

public class Test {
    public static synchronized void foo() {
        // complete method is synchronized on Test.class
    }
}

public class Test {
    public static void foo() {
        synchronized(Test.class) {
            // only a block is synchronized on Test.class
        }
    }
}
