public class Foo {
    void bar() {
        Exception e = new NullPointerException();
        e = new RuntimeException();
        throw e;
    }
}

public class Foo {
    void bar(String s) {
        Exception e = new RuntimeException();
        if (s.equals("throw")) {
            throw e;
        }
        e = new NullPointerException();
    }
}

public class Foo {
    void bar() {
        Exception e = new RuntimeException();
        e = new NullPointerException();
        throw e;
    }
}

public class Foo {
    void bar() {
        Exception e = new RuntimeException();
        if (e.toString().isEmpty())
            e = new NullPointerException();
        throw e;
    }
}

public class Foo {
    void bar(Exception e) {
        if (e.toString().isEmpty())
            e = new NullPointerException();
        throw e;
    }
}
