public class Complicated {
  public void example() {
    int x = 0, y = 1, z = 2, t = 2;
    boolean a = false, b = true, c = false, d = true;

    if (a && b || b && d) {
      if (y == z) {
        x = 2;
      } else if (y == t && !d) {
        x = 2;
      } else {
        x = 2;
      }
    } else if (c && d) {
      while (z < y) {
        x = 2;
      }
    } else if (a && !b) {
      for (int n = 0; n < t; n++) {
        x = 2;
      }
    } else {
      switch (x) {
      case 1:
        x = 2;
        break;

      case 2:
      case 3:
        x = 2;
        break;

      default:
        x = 2;
        break;
      }
    }
  }
}

