class Foo {
  void foo(){}
  void bar(){}
  void switchCase() {
    int x=0;
    switch (x) {
    case 1:
    case 2: foo(); break;
    default: bar(); break;
    }
  }
}


class Foo {
  void foo(){
    int x=0, y=1;
    boolean a, b;

    if (x > 2 || y < 4) {
      while (x++ < 10 && !(y-- < 0));
    } else if (a && b || x < 4) {
      return;
    }
  }
}


