    public final int BAR = 42;
}

public class Foo {
    public static final int BAR = 42;
}

public class Foo {
    public int BAR = 42;
}

public class Foo {
    public final String BAR = "42";
}

public class Foo {
    public final Thread BAR = new Thread();
}

