public class MyClass {
  private int positive = 1;

  public MyClass (int value) {
      // if negative keep default
      if (value > 0) {
          positive = value;
      }
  }
}

public class MyClass {
  private int positive;

  public MyClass (int value) {
      if (value > 0) {
          positive = value;
      } else {
          positive = 1;
      }
  }
}

public class pmd_inc {
    private int test;

    public pmd_inc() {
        this.test = 2;
    }

    public int get_test() {
        return this.test;
    }

    public void inc_test(int val) {
        this.test += val;
    }
}

public class MyClass {
    private long counter = 0;
    public <E> void run(final E entity, final Class<? extends TemplatedClass<E>> m) throws MyException {
        this.counter += 1;
    }
}

public class Foo {
    private static class Bar {
        private int i;
        Bar(final int i) {
            this.i = i;
        }
    }

    public int foo() {
        final Bar b = new Bar(1);
        b.i=0;
        return b.i;
    }
}
