public class Foo {
   public int bar(int x) {
      if (!(x == 0))
         return x;
      return 0;
   }
}

public class Foo {
   public int bar(int x) {
      if (!(x <= 0))
         return x;
      return 0;
   }
}

public class Foo {
   public int bar(int x) {
      if (x != 0)
         return x;
      return 0;
   }
}

public class Foo {
   public int bar(int x) {
      if (x > 0)
         return x;
      return 0;
   }
}

public class Foo {
    public void bar(int a, int b) {
        if (!(a != b)) { } // violation
        if (a == b) { } // ok
    }
}
