public class Foo {
    public static void bar() {
        boolean a, b = true;
        try { // total 8
            if (true) { // 2
                List buz = new ArrayList();
            }

            for(int i = 0; i < 19; i++) { // 3
                List buz = a ? new ArrayList() : null;
            }
        } catch(Exception e) {
            if (true) { // 2
                e.printStackTrace();
            }
        }

        int j = 0;
        if (true || a && b) { // 4
            j = 10;
            return;
        } else {
            j = 12;
        }

        j = a || b ? j + 1 : j; // 3

        while (j++ < 20) { // 2
            List buz = new ArrayList();
        }

        switch(j) { // 7
        case 1:
        case 2: break;
        case 3: j = 5; break;
        case 4: if (b && a) { bar(); } break;
        default: break;
        }

        do { // 3
            List buz = new ArrayList();
        } while (a && j++ < 30);

        if (b) { // 2
            return;
        }
    }
}

