// Switch Density = 5.0
public class SwitchDensity1 {
    public void foo(int i) {
        switch (i) {
            case 0:
            {
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
            }
        }
    }
}

// Switch Density = 5.0
public class SwitchDensity1 {
    public void foo(int i) {
        i = switch (i) {
            case 0:
            {
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
            }
        };
    }
}

// Switch Density = 5 / 3
public class SwitchDensity1 {
    public void foo(int i) {
        i = switch (i) {
            case 0, 1, 2:
            {
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
                System.err.println("I am a fish.");
            }
        };
    }
}

// Switch Density = 1.0
