class Test{
    Object obj = new Object(), objectWithDifferentName = obj;

    public void testPMD(){
        Object obj = new Object();
        String str = "str";

        //Case 1. : An Alarm is generated
        if(obj == null && !(obj instanceof String));

        //Case 3.
        if(obj == null && this.obj == null); //An Alarm is generated
        if(obj == null && this.objectWithDifferentName); //An Alarm isn't generated
        if(obj == null && objectWithDifferentName); //An Alarm isn't generated
    }
}

class Test{ public void testPMD(){ Boolean boo = null;
boolean result; result = boo == null && boo != true; // boo != true would throw NPE because of unboxing result = boo == null && boo != false; // same result = boo == null && boo == true; // same result = boo == null && boo == false; // same
result = boo != null && boo != true; // ok result = boo != null && boo != false; // ok result = boo != null && boo == true; // ok result = boo != null && boo == false; // ok
result = boo == null || boo != true; // ok result = boo == null || boo != false; // ok result = boo == null || boo == true; // ok result = boo == null || boo == false; // ok
result = boo != null || boo != true; // second expression will throw result = boo != null || boo != false; // second expression will throw result = boo != null || boo == true; // second expression will throw result = boo != null || boo == false; // second expression will throw } }

import java.util.*;

class Test {

    Test foo = new Test(), another = new Test();
    Map<String, String> map;
    String field;
    int idx;
    String[] arr, anotherArr;

    Object foo(Object o) {}

    public void testPMD() {
        //Case 1.
        if ((foo == null) != (another == null) || foo != null && !foo.equals(another)) {
            ;
        }

        //Case 2.
        if (map.remove(null) != null || map.remove("") != null) {
            ;
        }
        if (foo(foo) != null || foo(foo.field) != null) {
            ;
        }

        //Case 3.
        if (foo != null || foo == null && another != null && another.hashCode() == 0) {
            ;
        }

        //Case 4.
        if (null != arr[idx] || null == arr[idx] && !anotherArr[idx].isEmpty()) {
            ;
        }
    }
}
