import java.math.MathContext;
public class DemoMissingBreak {
    public MathContext foo(int precission) {
        MathContext mc = null;
        switch (precission) {
            case 32:
                mc = MathContext.DECIMAL32;
                break;
            case 64: 
                mc = MathContext.DECIMAL64;
                break;
            case 128:
                mc = MathContext.DECIMAL128;
                break;
            default:
                mc = new MathContext(precission);
        }
        return mc;
    }
}

import java.math.MathContext;
public class DemoMissingBreak {
    public MathContext foo(int precission) {
        MathContext mc = null;
        switch (precission) {
            case 32:
                mc = MathContext.DECIMAL32;
                break;
            case 64:
                mc = MathContext.DECIMAL64;
                break;
            default:
                mc = new MathContext(precission);
            case 128: // violation!!
                mc = MathContext.DECIMAL128;
                break;
        }
        return mc;
    }
}

record MyRecord(boolean b) {
    public MyRecord {
        int n = 8;
        // no fallthrough
        switch (n) {
            case 1:
                doSomething();
                break;
            default:
                break;
        }
        // implicit fallthrough
        switch (n) {
            case 1:
                doSomething();
            default:
        }
    }
}

