public class Foo {
  public void m() {
    switch (s) {
      case 0 :
        s2 = s ;
        // missing break here!
      case 1 :
        s2 = (short)(s + 1);
        break;
      default :
        s2 = (short)(s + 2);
        // and missing break here!
    }
  }
}

public class Foo {
    public static final int x1 = 1;
    public static final int x2 = 2;
    public static final int x3 = 3;

    public Object bar(int x, boolean condition) {
        switch (x)
        {
            case x1:
            case x2:
                if (condition)
                    return new Object();
                else
                    return null;
            case x3:
                if (condition)
                    return new Object();
                else
                    return null;
            default:
                return null;
        }
    }
}

public class Foo {
    public String bar() {
        switch (sign) {
        case  1:
            return "+";
        case  0:
            return "0";
        case -1:
            return "-";
        default:
            throw new IllegalArgumentException();
        }
    }
}

public class Foo {
    private boolean isAorB(MyEnum value)
    {
      switch (value)
      {
        case A:
        case B:
        {
          return true;
        }
        default:
        {
          return false;
        }
      }
    }
}

public class DemoMissingBreakContinue {
public DemoMissingBreakContinue() {
method();
}

private void method() {
    for (int i = 0; i < 10; i = i + 1) {
        switch (i) {
        case 1:
            break;
        case 2:
            continue; //PMD complains about missing break which would be unreachable code
        default:
            break;
        }
    }
}

}
