    void bar(Object x, boolean y) {
        if (y == true) {}
    }
}

class UnconditionalIfStatementBug {
    public static void main(String[] args) {
        boolean condition = true;
        if (condition) {            // violation line 4
            System.out.println("This will always print.");
        }
    }
}

class UnconditionalIfStatementBug {
    public static void main(String[] args) {
        boolean condition = false;
        for (String arg : args) {
            if ("bar".equals(arg)) {
                condition |= true;
            }
        }
        if (condition) {
            System.out.println("bar was given.");
        }
    }
}

class UnconditionalIfStatementBug {
    public static void main(String[] args) {
        boolean condition = args.length == 0;
        if (condition) {
            System.out.println("No args given.");
        }
    }
}
