import org.apache.commons.logging.Log;
import org.apache.commons.logging.LogFactory;

public class Foo {
    static final Log _LOG = LogFactory.getLog( Foo.class );
    void foo() {
        try {
        } catch (IllegalArgumentException iae) {
            _LOG.error(iae.getMessage(), iae);
        }
    }
}

