public class Foo {
    public void bar() {
        Thread t = new Thread();
        t.run();                // use t.start() instead
    }
}

public class Foo {
    public void bar() {
        new Thread().run();
    }
}

public class Foo {
    private Thread bar;
    public void bar(Foo foo) {
        foo.bar.run();
    }
}
