import java.util.HashSet;
import java.util.Set;

public class Foo {
    private final Set<String> hashSet = new HashSet<>();

    public void parseData(final String dataString) {
        final String[] myArray = dataString.split(",");

        for (final String element : myArray) {
            this.hashSet.add(element); // line 11 - false positive
        }
        // e.g. this.hashSet = Arrays.asList(myArray); won't compile
    }
}

import java.util.*;
public class Test {
  public static void main(String... args) {
    String[] entrySet = {"key1", "value1", "key2", "value2", "key3", "value3"};
    for (String key : keySet(entrySet)) {
      System.out.println(key);
    }
  }

  private static Collection<String> keySet(String... entrySet) {
    final List<String> result = new ArrayList<>(entrySet.length / 2);
    for (int i = 0; i < entrySet.length; i += 2) {
      result.add(entrySet[i]); // false positive here
    }
    return result;
  }
}

import java.util.ArrayList;
import java.util.List;

class Foo {
    public List<String> convertToNonEmptyList(String[] arr) {
        List<String> resultList = new ArrayList<>();
        for (String item: arr) {
            if (item.isEmpty()) {
                continue;
            }
            resultList.add(item);
        }
        return resultList;
    }

    public List<String> convertToNonEmptyList2(String[] arr) {
        List<String> resultList = new ArrayList<>();
        for (int i = 0; i < arr.length; i++) {
            if (arr[i].isEmpty()) {
                continue;
            }
            resultList.add(arr[i]);
        }
        return resultList;
    }
}
