import java.util.Optional;
public class Foo {
    int getLastSeen() { return 0; }
    public void bar(List<Foo> foos) {
        Optional<Foo> optionalResult = foos.stream()
            .filter(s -> s.getLastSeen() > 0)
            .findFirst();
    }
}

