rule.example:
// Ok
if (foo) {
    x++;
}

// Bad
if (foo)
    x++;

// Ok
if (foo) {
    x++;
} else {
    y++;
}

// Bad
if (foo)
    x++;
else
    y++;

// Ok
while (true) {
    x++;
}

// Bad
while (true)
    x++;

// Ok
for (var i = 0; i < 42; i++) {
    foo();
}

// Bad
for (var i = 0; i < 42; i++)
    foo();
