CREATE OR REPLACE
PACKAGE BODY Foo AS
	PROCEDURE bar1 IS BEGIN
    -- 1000 lines of code
	END bar1;
	PROCEDURE bar2 IS BEGIN
    -- 1000 lines of code
	END bar2;
    PROCEDURE bar3 IS BEGIN
    -- 1000 lines of code
	END bar3;


    PROCEDURE barN IS BEGIN
    -- 1000 lines of code
	END barn;
END;

CREATE OR REPLACE
TYPE BODY Foo AS
	 MEMBER PROCEDURE bar1 IS BEGIN
    -- 1000 lines of code
	END bar1;
	 MEMBER PROCEDURE bar2 IS BEGIN
    -- 1000 lines of code
	END bar2;
     MEMBER PROCEDURE bar3 IS BEGIN
    -- 1000 lines of code
	END bar3;


     MEMBER PROCEDURE barN IS BEGIN
    -- 1000 lines of code
	END barn;
END;

CREATE OR REPLACE
PACKAGE BODY Foo AS
	PROCEDURE bar1 IS BEGIN
    -- 1000 lines of code
	END bar1;
	PROCEDURE bar2 IS BEGIN
    -- 1000 lines of code
	END bar2;
    PROCEDURE bar3 IS BEGIN
    -- 1000 lines of code
	END bar3;


    PROCEDURE barN IS BEGIN
    -- 1000 lines of code
	END barn;
END;

CREATE OR REPLACE
PACKAGE Foo AS
	PROCEDURE bar1 ;
	PROCEDURE bar2 ;
        PROCEDURE bar3 ;

    ..

        PROCEDURE barN ;
END;


CREATE OR REPLACE PACKAGE pkg_too_many_fields AS
    C_CHAR_A CONSTANT CHAR(1 CHAR) := 'A';
    C_CHAR_B CONSTANT CHAR(1 CHAR) := 'B';
    ...
    C_CHAR_Z CONSTANT CHAR(1 CHAR) := 'Z';
END pkg_too_many_fields;

