PACKAGE BODY foo AS
PROCEDURE bar AS BEGIN	-- this is something more complex than it needs to be,
	if (y) THEN	-- it should be broken down into smaller methods or functions
		for j IN 0 .. j-1 LOOP
			if (j > r) THEN
				doSomething;
				while (f < 5 ) LOOP
					anotherThing;
					f := f - 27;
					END LOOP;
			else
				tryThis();
			END IF;
		END LOOP;
	END IF;
	if ( r - n > 45) THEN
		while (doMagic) LOOP
			findRabbits;
